import React, { useState } from "react";
import { NavLink } from "react-router-dom";
// import { socialMediaUrl } from "../Details";
import { personalDetails } from "../Details";

function Header() {
  const { name } = personalDetails;
  const [isOpen, setIsOpen] = useState(false);

  const toggleClass = () => {
    setIsOpen(!isOpen);
  };

  return (
    <header className="container mx-auto md:flex justify-between py-2 max-width">
      <div className="flex justify-between items-center py-2 md:py-10">
        <NavLink to="/">
          <h1 className="text-2xl bg-clip-text bg-gradient text-transparent md:text-3xl xl:text-3xl xl:leading-tight font-bold">
            {name}
          </h1>
        </NavLink>
        <div onClick={toggleClass} className="cursor-pointer">
          <svg
            className="stroke-dark-heading dark:stroke-white md:hidden"
            width="25"
            height="20"
            viewBox="0 0 16 13"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              d="M1.4375 1.3125H14.5625M1.4375 11.3125H14.5625H1.4375ZM1.4375 6.3125H14.5625H1.4375Z"
              strokeWidth="1.875"
              strokeLinecap="round"
              strokeLinejoin="round"
            />
          </svg>
        </div>
      </div>
      <nav
        className={`${
          !isOpen ? "hidden" : null
        } text-center md:flex justify-between`}
      >
        <ul className="dark:text-light-content font-medium md:flex items-center md:space-x-5 md:mr-10">
          <li className="pb-1 md:pb-0">
            <NavLink
              to="/"
              onClick={toggleClass}
              className="nav-link"
              activeClassName="active"
              style={navLinkStyles}
            >
              Home
            </NavLink>
          </li>
          <li className="pb-1 md:pb-0">
            <NavLink
              to="/groupProject"
              onClick={toggleClass}
              className="nav-link"
              activeClassName="active"
              style={navLinkStyles}
            >
              Group Project
            </NavLink>
          </li>
          <li className="pb-1 md:pb-0">
            <NavLink
              to="/individualProject"
              onClick={toggleClass}
              className="nav-link"
              activeClassName="active"
              style={navLinkStyles}
            >
              Individual Projects
            </NavLink>
          </li>
          <li className="pb-1 md:pb-0">
            <NavLink
              to="/internationalProject"
              onClick={toggleClass}
              className="nav-link"
              activeClassName="active"
              style={navLinkStyles}
            >
              International Project
            </NavLink>
          </li>
        </ul>
      </nav>
    </header>
  );
}

const navLinkStyles = {
  fontSize: "1rem",
  transition: "font-size 0.3s ease",
};

export default Header;
