import literatureImage from "../assets/images/literaturestudy.png";

const groupProjectData = {
  group1: {
    heading: "Noise control",
    subHeading: "Semester6 Media Design - Group project",
  },
  group2: {
    heading: "Project Approach - Design thinking method",
    subHeading: "Discover phase",
  },

  firstPhaseHeading: "Discover Phase",

  firstPhase: {
    question:
      "How can I identify and address potential design issues on my portfolio website before publishing it?",
    subHeading: "Methods:",
    methods: "Usability testing",
    text: "",
    subheadingWhy: "Why:",
    why: "I decided to do a usability test for my website because I want to find out if there are any problems that users might face. By doing this test before launching the website, I can fix any issues and make improvements based on the feedback I receive. This way, I can make sure that the website is easy to use and provides a good experience for users",
    resultHeading: "Result: ",
    results:
      "I made some small changes to the contact page based on the feedback I received. Additionally, I added some special effects to make the portfolio website look more attractive.",
    image: literatureImage,
  },

  group3: {
    question: "What insights are valuable to teachers?",
    subHeading: "Methods->",
    methods: "Literature research, Document analysis",
    text: "To divide the literature research fairly, we randomly assigned different topics to each team member. I focused on gathering insights that would be useful to gym teachers regarding the impact of loud sounds on hearing and stress. We also carefully examined the research materials provided by Sorama. Specifically, I extensively studied the research related to insights valuable to PE teachers.",
    whyLiterature:
      "Why is literature research important & how has it helped us?",
    literatureAnswer:
      "Doing literature research was important to learn about existing knowledge on the topic. I read the research conducted by Sorama and discovered interesting insights. Specifically, I focused on gathering information that would be helpful to gym teachers, showing them how loud sounds can affect hearing and stress. This research helped us understand the valuable insights that can be shared with PE teachers in the gym hall, raising awareness about the potential negative effects of being exposed to high noise levels for a long time. These insights guided us in determining what information can be provided to PE teachers.",
    resultHeading: "The Results",
    results: "The findings revealed a strong relationship between A and B...",
    conclusion: "In conclusion, it is important to consider...",
  },
  group4: {
    discoverPhase:
      "During the project's discovery phase, the team took a comprehensive approach to gather valuable insights. we started by conducting literature research to learn from existing knowledge on the topic, focusing on Sorama's research about the effects of loud sounds on hearing and stress. This research helped us gain a better understanding of the subject and its implications. My main responsibility was to find useful insights that would be valuable to physical education teachers, especially regarding raising awareness about potential negative effects.",
    heading: "Conclusion:",
    conclusion:
      "During the Discover phase of the design thinking process, we actively gathered and examined crucial information regarding the preferences and priorities of PE teachers. Additionally, we explored the dynamic nature of sound levels throughout their daily schedules. Our comprehensive approach involved studying relevant literature, analyzing documents, researching competitors, and engaging in insightful user interviews. As a result, we acquired valuable insights into the needs and challenges faced by PE teachers. <br/><br/> Now equipped with this knowledge, we are ready to progress to the next step—the Define phase. Building upon the findings and results obtained during the Discover phase, our focus shifts towards obtaining a deeper understanding of the problem at hand. By utilizing the gathered information, we aim to clarify and define the specific design challenges that must be addressed. This holistic comprehension will be instrumental in the development of effective and impactful design solutions.",
  },
  // Add more question objects as needed
};

export default groupProjectData;
