import React from "react";

const CircleComponent = () => {
  const phases = ["Discover", "Define", "Ideate", "Prototype", "Test"];

  return (
    <div
      style={{
        display: "flex",
        marginTop: "4rem",
        marginBottom: "-5rem",
        position: "relative",
      }}
    >
      {phases.map((phase, index) => (
        <div
          key={index}
          style={{
            width: "150px",
            height: "150px",
            borderRadius: "50%",
            backgroundColor: "white",
            border: "2px solid black",
            marginRight: "10px",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            position: "relative",
            transition: "transform 0.3s, box-shadow 0.3s",
            cursor: "pointer",
          }}
          onMouseEnter={(e) => {
            e.target.style.transform = "scale(1.1)";
            e.target.style.boxShadow = "0px 0px 10px 2px rgba(0, 0, 0, 0.3)";
          }}
          onMouseLeave={(e) => {
            e.target.style.transform = "scale(1)";
            e.target.style.boxShadow = "none";
          }}
        >
          <span>{phase}</span>
          {index > 0 && (
            <span
              style={{
                position: "absolute",
                top: "50%",
                left: "-80px",
                transform: "translateY(-50%)",
                height: "4px",
                width: "70px",
                backgroundColor: "black",
              }}
            />
          )}
          {index > 0 && index < phases.length - 1 && (
            <span
              style={{
                position: "absolute",
                top: "calc(50% - 40px)",
                left: "-40px",
                height: "80px",
                width: "4px",
                backgroundColor: "black",
              }}
            />
          )}
          {index < phases.length - 1 && (
            <span
              style={{
                position: "absolute",
                top: "50%",
                right: "-80px",
                transform: "translateY(-50%)",
                height: "4px",
                width: "70px",
                backgroundColor: "black",
              }}
            />
          )}
          {index < phases.length - 1 && index > 0 && (
            <span
              style={{
                position: "absolute",
                top: "calc(50% - 40px)",
                right: "-40px",
                height: "80px",
                width: "4px",
                backgroundColor: "black",
              }}
            />
          )}
          {index > 0 && (
            <div
              style={{
                position: "absolute",
                top: "calc(50% - 10px)",
                left: "-20px",
                height: "20px",
                width: "20px",
                backgroundColor: "black",
                borderRadius: "50%",
              }}
            />
          )}
          {index < phases.length - 1 && (
            <div
              style={{
                position: "absolute",
                top: "calc(50% - 10px)",
                right: "-20px",
                height: "20px",
                width: "20px",
                backgroundColor: "black",
                borderRadius: "50%",
              }}
            />
          )}
        </div>
      ))}
    </div>
  );
};

export default CircleComponent;
